import React, { Component } from 'react';
import { DropdownMenu } from '@radix-ui/themes';
import { TextField, Flex, Button } from '@radix-ui/themes';

export default class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTerm: '',
      products: [],
      filteredProducts: [],
    };
  }

  componentDidMount() {
    // Fetch product data from the API (replace 'api-url' with your actual API URL)
    fetch('http://localhost:8000/api/products')
      .then((response) => response.data.data)
      .then((data) => {
        this.setState({ products: data });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
      });
  }

  handleInputChange = (event) => {
    const searchTerm = event.target.value;
    this.setState({ searchTerm });

    // Filter the products based on the search term
    const filtered = this.state.products.filter((product) =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    this.setState({ filteredProducts: filtered });
  };

  render() {
    return (
      <Flex>
        <DropdownMenu>
          <DropdownMenu.Trigger>
            <Flex>
              <TextField.Root radius="medium" size="3"  style={{ width: '300px'}}>
                  <TextField.Input value={this.state.searchTerm} onChange={this.handleInputChange} placeholder="Search..." size="2" />
              </TextField.Root>
              <Button radius='medium' style={{ 
                borderTopLeftRadius : '0',
                borderBottomLeftRadius : '0',
              }}>
                Search
              </Button>
            </Flex>
          </DropdownMenu.Trigger>
          <DropdownMenu.Content>
            {this.state.filteredProducts.map((product) => (
              <DropdownMenu.Item key={product.id}>{product.name}</DropdownMenu.Item>
            ))}
          </DropdownMenu.Content>
        </DropdownMenu>
      </Flex>
    );
  };
}
