import { Component } from "react";
import { TextField, Button, Flex } from "@radix-ui/themes";
import axios from "axios";

export default class SearchDropdown {
    search_products() {
        let data;
        let pdts;
        axios.get('http://localhost:8000/api/products')
          .then(response => {
            pdts = response.data;
            data = pdts.data;
            console.log(response);
            console.log(data)
            this.setState({
              products: data
            });
          })
          .catch(error => {
            console.log("Failed to get products from api");
            console.log(error);
          })
    }

    render () {
        return (
            <Flex style={{ width: '400px'}}>
                <TextField.Root radius="medium" size="3"  style={{ width: '300px'}}>
                    <TextField.Input placeholder="Search..." size="2" />
                </TextField.Root>
                <Button color='indigo' radius='medium'>Search</Button>
            </Flex>
        )
    }

}
