import React, { Component } from 'react';
import { DropdownMenu, DropdownMenuTrigger, DropdownMenuContent } from '@radix-ui/themes';
import { TextField } from '@radix-ui/themes';

export default class SearchBar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTerm: '',
      products: [],
      filteredProducts: [],
    };
  }

  componentDidMount() {
    // Fetch product data from the API (replace 'api-url' with your actual API URL)
    fetch('http://localhost:8000/api/products')
      .then((response) => response.json())
      .then((data) => {
        this.setState({ products: data });
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
      });
  }

  handleInputChange = (event) => {
    const searchTerm = event.target.value;
    this.setState({ searchTerm });

    // Filter the products based on the search term
    const filtered = this.state.products.filter((product) =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    this.setState({ filteredProducts: filtered });
  };

  render() {
    const { searchTerm, filteredProducts } = this.state;

    return (
      <DropdownMenu>
        <DropdownMenuTrigger>
          <input
            type="text"
            value={searchTerm}
            onChange={this.handleInputChange}
            placeholder="Search..."
          />
        </DropdownMenuTrigger>
        <DropdownMenuContent>
          {filteredProducts.map((product) => (
            <div key={product.id}>{product.name}</div>
          ))}
        </DropdownMenuContent>
      </DropdownMenu>
    );
  }
}
