import * as React from 'react';
import classNames from 'classnames';
import { kbdPropDefs } from './kbd.props';
import { extractMarginProps, withMarginProps, withBreakpoints } from '../helpers';
const Kbd = React.forwardRef((props, forwardedRef) => {
  const {
    rest: marginRest,
    ...marginProps
  } = extractMarginProps(props);
  const {
    className,
    size = kbdPropDefs.size.default,
    ...kbdProps
  } = marginRest;
  return React.createElement("kbd", {
    ...kbdProps,
    ref: forwardedRef,
    className: classNames('rt-Kbd', className, withBreakpoints(size, 'rt-r-size'), withMarginProps(marginProps))
  });
});
Kbd.displayName = 'Kbd';
export { Kbd };
