import React, { Component, useState, useEffect } from "react";
import axios from "axios";
import { Grid, Button } from "@radix-ui/themes";

export default class LaptopsTab extends Component {

  state = { products: [],}

  componentDidMount() {
    let data;
    axios.get('http://localhost:8000/api/get-category-pdts?category=1')
      .then(response => {
        data = response.data;
        this.setState({
          products: data
        });
      })
      .catch(error => {
        console.log("Failed to get products from api");
        console.log(error);
      })
  }


  render() {
    return (
    <Grid>
      {this.state.products.map((product, index) => (
          <div
            key={index}
            style={{
              height: "300px",
              width: "300px",
              color: "white",
              margin: "20px",
              borderRadius: "20px"
            }}
          >
            <div
              style={{
                height: "250px",
                width: "300px",
                backgroundImage: product.image,
                backgroundSize: "cover",
                color: "white"
              }}
            ></div>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                alignItems: "stretch",
                width: "100%"
              }}
            >
              <Button>Purchase</Button>
              <Button>Add to Cart</Button>
              <Button>More...</Button>
            </div>
          </div>
        ))
      }
    </Grid>
  );
}
}
