import React, { useState, useEffect } from 'react';
import { DropdownMenu, DropdownTrigger, DropdownContent } from '@radix-ui/react-dropdown-menu';
import { TextField, Flex, Button } from '@radix-ui/themes';


const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  // Fetch product data from the API (replace 'api-url' with your actual API URL)
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('api-url/products');
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  // Function to handle search input change
  const handleInputChange = (event) => {
    const searchTerm = event.target.value;
    setSearchTerm(searchTerm);

    // Filter the products based on the search term
    const filtered = products.filter((product) =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    setFilteredProducts(filtered);
  };

  return (
    <DropdownMenu>
      <DropdownTrigger>
        <Flex>
          <TextField.Root radius="medium" size="3"  style={{ width: '300px'}}>
              <TextField.Input value={this.state.searchTerm} onChange={this.handleInputChange} placeholder="Search..." size="2" />
          </TextField.Root>
          <Button radius='medium' style={{ 
            borderTopLeftRadius : '0',
            borderBottomLeftRadius : '0',
          }}>
            Search
          </Button>
        </Flex>
      </DropdownTrigger>
      <DropdownContent>
        {filteredProducts.map((product) => (
          <div key={product.id}>{product.name}</div>
        ))}
      </DropdownContent>
    </DropdownMenu>
  );
};

export default SearchBar;
