import { 
    Flex, 
    Button,
    Box,
    Separator,
    Heading,
    TextField,
    Link,
} from '@radix-ui/themes';

import { useState, useEffect } from 'react';


export default function TopBar1 () {

    const [searchTerm, setSearchTerm] = useState('');
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);

    // Fetch product data from the API (replace 'api-url' with your actual API URL)
    useEffect(() => {
        const fetchProducts = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/api/products');
            const data = await response.json();
            setProducts(data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
        };

        fetchProducts();
    }, []);

    // Function to handle search input change
    const handleInputChange = (event) => {
        const searchTerm = event.target.value;
        setSearchTerm(searchTerm);

        // Filter the products based on the search term
        const filtered = products.filter((product) =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase())
        );

        setFilteredProducts(filtered);
    };

    return (
        <Box width="100%" height='1'>
            <Flex gap="9" align="center">
                <Heading as="h2">Thommy's Store</Heading>
                <Flex gap='9'></Flex>
                <Flex gap='9'></Flex>
                <Flex>
                    <TextField.Root radius="medium" size="3"  style={{ width: '300px'}}>
                        <TextField.Input placeholder="Search..." size="2" />
                    </TextField.Root>
                    <Button radius='medium' style={{ 
                    borderTopLeftRadius : '0',
                    borderBottomLeftRadius : '0',
                    }}>
                    Search
                    </Button>
                </Flex>
                <Flex gap='9'></Flex>
                <Flex gap='9'></Flex>
                <Flex gap="5">
                    <Separator orientation="vertical" />
                    <Link color="indigo" href="#">
                    About
                    </Link>
                    <Separator orientation="vertical" />
                    <Link color="indigo" href="#">
                    Contact Us
                    </Link>
                    <Separator orientation="vertical" />
                    <Link color="indigo" href="#">
                    Report
                    </Link>
                </Flex>
            </Flex>
            <Separator my="3" size="4" />
        </Box>
    )
}
