import {useState, useEffect} from "react";
import axios from "axios";

import {
    Grid,
    Button
} from '@radix-ui/themes';

export default function LaptopsTab () {

    const [products, setProducts] = useState(null);


    function getProducts() {
        return fetch("api/get-category-pdts/category=1")
        .then((response) => setProducts(response));
    }
    return (
        <Grid>
            {getProducts().map(product => 
                <div style={{
                    height:'300px', 
                    width:'300px', 
                    color: 'white',
                    margin: '20px',
                    borderRadius: '20px',
                    }}>
                    <div style={{
                        height:'250px', 
                        width:'300px', 
                        backgroundImage: product.image,
                        backgroundSize: 'cover',
                        color: 'white',
                        }}>
                    </div>
                    <div style={{
                        display: 'flex', 
                        flexDirection: 'row',
                        alignItems: 'stretch',
                        width: '100%'}}>
                        <Button>Purchase</Button>
                        <Button>Add to Cart</Button>
                        <Button>More...</Button>
                    </div>
                </div>
            )}
        </Grid>
    )
}
