import {useState, useEffect} from "react";
import axios from "axios";

import {
    Grid,
    Button
} from '@radix-ui/themes';

export default function LaptopsTab () {

    const [products, setProducts] = useState(null);
    const [pdt, setPdt] = useState({
        id: 0,
        name: '',
        description: '',
        unit_price: '',
        image: '',
        created_at: '',
        category: '',
    });

    useEffect(() => {
        getProducts()
            } ,[])


    function getProducts() {
        axios({
            method: "GET",
            url:"/api/get-category-pdts?category=1",
            }).then((response)=>{
            const data = response.data
            setProducts(data)
            }).catch((error) => {
            if (error.response) {
                console.log(error.response);
                console.log(error.response.status);
                console.log(error.response.headers);
                }
            })
    }
    return (
        <Grid>
            {products && products.map(product => 
                <div style={{
                    height:'300px', 
                    width:'300px', 
                    color: 'white',
                    margin: '20px',
                    }}>
                    <div style={{
                        height:'250px', 
                        width:'300px', 
                        backgroundImage: product.image,
                        backgroundSize: 'cover',
                        color: 'white',
                        }}>
                    </div>
                    <div style={{
                        display: 'flex', 
                        flexDirection: 'row',
                        alignItems: 'stretch',
                        width: '100%'}}>
                        <Button>Purchase</Button>
                        <Button>Add to Cart</Button>
                        <Button>More...</Button>
                    </div>
                </div>
            )}
        </Grid>
    )
}
